using System;
using AdventOfCode2022.Day6;
using Shouldly;

namespace tests.Day6
{
    [TestClass]
    public class MarkerFinderTests
    {
        [TestMethod]
        public void findMarkerSample1()
        {
            MarkerFinder.FindMarker("mjqjpqmgbljsphdztnvjfqwrcgsmlb").ShouldBe(7);
        }

        [TestMethod]
        public void findMarkerSample2()
        {
            MarkerFinder.FindMarker("bvwbjplbgvbhsrlpgdmjqwftvncz").ShouldBe(5);
        }
        [TestMethod]
        public void findMarkerSample3()
        {
            MarkerFinder.FindMarker("nppdvjthqldpwncqszvftbrmjlhg").ShouldBe(6);
        }
        [TestMethod]
        public void findMarkerSample4()
        {
            MarkerFinder.FindMarker("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg").ShouldBe(10);
        }

        [TestMethod]
        public void findMarkerSample5()
        {
            MarkerFinder.FindMarker("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw").ShouldBe(11);
        }

        [TestMethod]
        public void findMarkerInput()
        {
            MarkerFinder.FindMarker("")
                .ShouldBe(1833);
        }

        [TestMethod]
        public void findMessageMarkerSample()
        {
            MarkerFinder.FindStartOfMessageMarker("mjqjpqmgbljsphdztnvjfqwrcgsmlb").ShouldBe(19);
        }

        [TestMethod]
        public void findMessageMarkerInput()
        {
            MarkerFinder.FindStartOfMessageMarker("")
                .ShouldBe(3425);
        }
    }
}

